import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';

function Navbar() {
  const [categories, setCategories] = useState([]);
  const [search, setSearch] = useState('');
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role'); // Store the user role
  const history = useHistory();

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const res = await axios.get('http://localhost:3001/api/categories');
        setCategories(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchCategories();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role'); // Remove user role on logout
    history.push('/login');
  };

  const handleSearch = (e) => {
    e.preventDefault();
    history.push(`/search?query=${search}`);
  };

  return (
    <nav className="bg-gray-900 p-4 shadow-lg">
      <div className="container mx-auto flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <Link to="/" className="text-white text-xl font-bold">ðŸš— Auto Parts</Link>
          <div className="relative group">
            <div className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">
              Categories
            </div>
            <div className="absolute mt-2 w-48 bg-white rounded shadow-lg z-10 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
              {categories.map(category => (
                <Link
                  key={category._id}
                  to={`/category/${category.name}`}
                  className="block px-4 py-2 text-gray-800 hover:bg-gray-100"
                >
                  {category.name}
                </Link>
              ))}
            </div>
          </div>
        </div>
        <form onSubmit={handleSearch} className="flex items-center space-x-4">
          <input
            type="text"
            placeholder="Search parts..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="px-3 py-2 rounded text-gray-800"
          />
          <button
            type="submit"
            className="bg-blue-500 text-white px-3 py-2 rounded hover:bg-blue-600 transition duration-300"
          >
            Search
          </button>
        </form>
        <div className="flex items-center space-x-4">
          {token ? (
            <>
              {role === 'admin' && <Link to="/add-part" className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">Add Part</Link>}
              <Link to="/profile" className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">Profile</Link>
              <Link to="/cart" className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">Cart</Link>
              <button onClick={handleLogout} className="bg-red-500 text-white px-3 py-2 rounded hover:bg-red-600 transition duration-300">Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">Login</Link>
              <Link to="/register" className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">Register</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
