import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';

function Navbar() {
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');
  const history = useHistory();
  const [searchVisible, setSearchVisible] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    history.push('/login');
  };

  const toggleSearchBar = () => {
    setSearchVisible(!searchVisible);
  };

  return (
    <header className="bg-gray-900 text-white p-4 shadow-lg">
      <div className="container mx-auto flex justify-between items-center">
        {/* Logo and Main Navigation */}
        <div className="flex items-center space-x-6">
          <h1 className="text-2xl font-bold tracking-wide">
            <Link to="/" className="text-white hover:text-indigo-400 transition duration-300">JAC Auto Parts</Link>
          </h1>
          <nav className="hidden md:flex space-x-4">
            <Link to="/products" className="text-gray-300 hover:text-white transition duration-300">Products</Link>
            <Link to="/contact" className="text-gray-300 hover:text-white transition duration-300">Contact</Link>
          </nav>
        </div>

        {/* Search Bar */}
        <div className={`relative flex-1 mx-4 ${searchVisible ? 'block' : 'hidden'} md:block`}>
          <input
            type="search"
            placeholder="Search products..."
            className="w-full p-2 bg-gray-800 text-gray-100 border border-gray-700 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-300"
          />
        </div>

        {/* User Actions */}
        <div className="flex items-center space-x-4">
          <button onClick={toggleSearchBar} className="text-gray-300 hover:text-white md:hidden focus:outline-none">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16.293 17.293a1 1 0 011.414 0l4 4a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414zM6 10a4 4 0 11-8 0 4 4 0 018 0zM16 10a6 6 0 11-12 0 6 6 0 0112 0z"></path>
            </svg>
          </button>
          {token ? (
            <>
              {role === 'admin' && <Link to="/create-detail" className="text-gray-300 hover:text-white transition duration-300">Add Part</Link>}
              <Link to="/profile" className="text-gray-300 hover:text-white transition duration-300">Profile</Link>
              <Link to="/cart" className="text-gray-300 hover:text-white transition duration-300">Cart</Link>
              <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition duration-300">
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="text-gray-300 hover:text-white transition duration-300">Login</Link>
              <Link to="/register" className="text-gray-300 hover:text-white transition duration-300">Register</Link>
            </>
          )}
        </div>
      </div>
    </header>
  );
}

export default Navbar;
