import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

function Profile() {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    const fetchProfile = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:3001/api/profile', { headers: { Authorization: `Bearer ${token}` } });
        setProfile(res.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, [location]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="loader ease-linear rounded-full border-8 border-t-8 border-gray-200 h-32 w-32"></div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="text-2xl text-gray-700">Profile not found</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 bg-white shadow-lg rounded-lg mt-12">
      <h1 className="text-4xl font-bold text-gray-800 mb-6 text-center">Profile</h1>
      <div className="bg-gray-100 p-6 rounded-lg">
        <div className="flex flex-col md:flex-row items-center mb-6">
          <img
            src={profile.imageUrl || 'https://via.placeholder.com/150'}
            alt="Profile"
            className="w-32 h-32 rounded-full shadow-lg md:mr-6 mb-4 md:mb-0"
          />
          <div>
            <p className="text-2xl text-gray-700 mb-2"><strong>Name:</strong> {profile.name}</p>
            <p className="text-2xl text-gray-700 mb-2"><strong>Phone:</strong> {profile.phone}</p>
            <p className="text-2xl text-gray-700"><strong>Role:</strong> {profile.role}</p>
          </div>
        </div>
        <div className="flex justify-center">
          <button className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition duration-300">Edit Profile</button>
        </div>
      </div>
    </div>
  );
}

export default Profile;
