import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory, useParams } from 'react-router-dom';

function CreateDetail() {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    machine: '',
    imageUrl: '',
    machineImage: null // New state for machine image
  });
  const { name, description, price, machine, imageUrl, machineImage } = formData;
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const history = useHistory();
  const { id } = useParams();

  useEffect(() => {
    if (id) {
      const fetchDetail = async () => {
        try {
          const res = await axios.get(`http://localhost:3001/api/details/${id}`);
          setFormData(res.data);
          setIsEditing(true);
        } catch (err) {
          console.error('Fetch Detail error:', err);
          setError('Failed to fetch detail');
        }
      };
      fetchDetail();
    }
  }, [id]);

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'file' ? files[0] : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    if (!name || !description || !price || !machine || !imageUrl) {
      setError('All fields are required');
      return;
    }
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };
      const url = isEditing
        ? `http://localhost:3001/api/details/${id}`
        : 'http://localhost:3001/api/details';

      const method = isEditing ? 'put' : 'post';

      // Handle image upload
      const formDataToSend = new FormData();
      formDataToSend.append('name', name);
      formDataToSend.append('description', description);
      formDataToSend.append('price', price);
      formDataToSend.append('machine', machine);
      formDataToSend.append('imageUrl', imageUrl);
      if (machineImage) {
        formDataToSend.append('machineImage', machineImage);
      }

      await axios[method](url, formDataToSend, { headers });
      history.push('/');
      alert(`${isEditing ? 'Detail updated' : 'Detail created'}`);
    } catch (err) {
      console.error(isEditing ? 'Detail update error:' : 'Detail creation error:', err);
      setError(err.response?.data?.message || 'Failed to save detail');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-6 bg-white shadow-lg rounded-xl mt-12 max-w-lg border border-gray-200">
      <h1 className="text-4xl font-bold mb-8 text-center text-gray-800">{isEditing ? 'Edit Detail' : 'Create Detail'}</h1>
      <form onSubmit={handleSubmit} className="space-y-6">
        {[
          { id: 'name', label: 'Name', type: 'text', value: name, placeholder: 'Enter product name' },
          { id: 'machine', label: 'Machine', type: 'text', value: machine, placeholder: 'Enter machine name' },
          { id: 'description', label: 'Description', type: 'textarea', value: description, placeholder: 'Enter product description', className: 'h-36 resize-none' },
          { id: 'price', label: 'Price', type: 'number', value: price, placeholder: 'Enter product price' },
          { id: 'imageUrl', label: 'Image URL', type: 'text', value: imageUrl, placeholder: 'Enter image URL' }
        ].map(({ id, label, type, value, placeholder, className }) => (
          <div key={id} className="flex flex-col gap-2">
            <label className="text-lg text-gray-700 font-semibold" htmlFor={id}>{label}</label>
            {type === 'textarea' ? (
              <textarea
                id={id}
                name={id}
                placeholder={placeholder}
                value={value}
                onChange={handleChange}
                className={`border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-300 ${className}`}
                aria-required="true"
                aria-invalid={error && !value ? "true" : "false"}
              />
            ) : (
              <input
                type={type}
                id={id}
                name={id}
                placeholder={placeholder}
                value={value}
                onChange={handleChange}
                className="border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-300"
                aria-required="true"
                aria-invalid={error && !value ? "true" : "false"}
              />
            )}
          </div>
        ))}
        <div className="flex flex-col gap-2">
          <label className="text-lg text-gray-700 font-semibold" htmlFor="machineImage">Machine Image</label>
          <input
            type="file"
            id="machineImage"
            name="machineImage"
            accept="image/*"
            onChange={handleChange}
            className="border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-300"
            aria-required="true"
          />
        </div>
        <div className="text-center mt-8">
          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 px-6 rounded-lg bg-indigo-600 text-white text-lg font-semibold hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-300 shadow-md ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
            aria-busy={loading ? "true" : "false"}
          >
            {isEditing ? 'Update Detail' : 'Create Detail'}
          </button>
          {error && <div className="text-red-500 mt-4 text-center text-sm">{error}</div>}
        </div>
      </form>
    </div>
  );
}

export default CreateDetail;
