import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { FiChevronDown, FiChevronUp } from 'react-icons/fi';
import { MdCategory, MdCheckBox, MdCheckBoxOutlineBlank } from 'react-icons/md';

function FilterSidebar({ machines, selectedFilters, onFilterChange }) {
  const [expandedMachines, setExpandedMachines] = useState({});
  const [expandedCategories, setExpandedCategories] = useState({});

  const toggleMachine = (machineId) => {
    setExpandedMachines(prev => ({
      ...prev,
      [machineId]: !prev[machineId]
    }));
  };

  const toggleCategory = (machineId, categoryName) => {
    setExpandedCategories(prev => ({
      ...prev,
      [`${machineId}-${categoryName}`]: !prev[`${machineId}-${categoryName}`]
    }));
  };

  const handleCheckboxChange = (machineId, categoryName, option) => {
    const newFilters = { ...selectedFilters };

    if (!newFilters[machineId]) {
      newFilters[machineId] = {};
    }

    if (!newFilters[machineId][categoryName]) {
      newFilters[machineId][categoryName] = [];
    }

    const currentOptions = newFilters[machineId][categoryName];
    if (currentOptions.includes(option)) {
      newFilters[machineId][categoryName] = currentOptions.filter(o => o !== option);
    } else {
      newFilters[machineId][categoryName].push(option);
    }

    onFilterChange(newFilters);
  };

  return (
    <aside className="w-full lg:w-1/4 p-6 bg-gradient-to-r from-gray-100 via-white to-gray-100 shadow-md rounded-lg">
      {machines.length > 0 ? (
        machines.map(machine => (
          <div key={machine._id} className="mb-6 border border-gray-300 rounded-lg p-4 bg-white shadow-sm hover:shadow-lg transition-shadow duration-300">
            <div className="flex items-start mb-4">
              <img 
                src={machine.imageUrl} 
                alt={machine.name} 
                className="w-24 h-24 object-cover rounded-lg border-2 border-gray-200 shadow-md transition-transform duration-300 transform hover:scale-105"
              />
              <button 
                onClick={() => toggleMachine(machine._id)}
                className="flex-grow text-left text-gray-800 font-bold text-xl ml-4 hover:text-blue-600 transition-colors duration-300"
              >
                {machine.name}
              </button>
              {expandedMachines[machine._id] ? (
                <FiChevronUp className="w-6 h-6 ml-2 text-gray-600 transition-transform duration-300" />
              ) : (
                <FiChevronDown className="w-6 h-6 ml-2 text-gray-600 transition-transform duration-300" />
              )}
            </div>
            {expandedMachines[machine._id] && (
              <div className="pl-4">
                {machine.categories.map(category => (
                  <div key={category.name} className="mb-4">
                    <button 
                      onClick={() => toggleCategory(machine._id, category.name)}
                      className="w-full flex justify-between items-center text-gray-700 font-semibold text-lg mb-2 hover:text-blue-600 transition-colors duration-300"
                    >
                      <div className="flex items-center">
                        <MdCategory className="w-5 h-5 mr-2 text-gray-500" />
                        {category.name}
                      </div>
                      {expandedCategories[`${machine._id}-${category.name}`] ? (
                        <FiChevronUp className="w-5 h-5 text-gray-600 transition-transform duration-300" />
                      ) : (
                        <FiChevronDown className="w-5 h-5 text-gray-600 transition-transform duration-300" />
                      )}
                    </button>
                    {expandedCategories[`${machine._id}-${category.name}`] && (
                      <div className="pl-4 space-y-2">
                        {category.options.map(option => (
                          <label key={option} className="flex items-center">
                            <input
                              type="checkbox"
                              className="form-checkbox h-5 w-5 text-blue-600 border-gray-300 rounded-sm focus:ring-blue-500"
                              checked={
                                selectedFilters[machine._id]?.[category.name]?.includes(option) || false
                              }
                              onChange={() => handleCheckboxChange(machine._id, category.name, option)}
                            />
                            {selectedFilters[machine._id]?.[category.name]?.includes(option) ? (
                              <MdCheckBox className="ml-3 text-gray-700" />
                            ) : (
                              <MdCheckBoxOutlineBlank className="ml-3 text-gray-700" />
                            )}
                            <span className="ml-2 text-gray-700 text-sm">{option}</span>
                          </label>
                        ))}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        ))
      ) : (
        <p className="text-gray-500 text-center">No machines available for filtering.</p>
      )}
    </aside>
  );
}

FilterSidebar.propTypes = {
  machines: PropTypes.arrayOf(
    PropTypes.shape({
      _id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
      categories: PropTypes.arrayOf(
        PropTypes.shape({
          name: PropTypes.string.isRequired,
          options: PropTypes.arrayOf(PropTypes.string.isRequired).isRequired
        })
      ).isRequired
    })
  ).isRequired,
  selectedFilters: PropTypes.object.isRequired,
  onFilterChange: PropTypes.func.isRequired
};

export default FilterSidebar;
