import React, { useEffect, useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import axios from 'axios';

function DetailCards() {
  const [details, setDetails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);
  const [features, setFeatures] = useState([]);
  const [selectedFilters, setSelectedFilters] = useState({
    category: '',
    priceMin: 0,
    priceMax: 15000,
    availability: [],
    brands: [],
    features: []
  });
  const [sortOption, setSortOption] = useState('popularity');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const location = useLocation();

  // Function to fetch details based on current filters and sort options
  const fetchData = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:3001/api/details', {
        params: { ...selectedFilters, sort: sortOption, page }
      });
      setDetails(response.data.details || []);
      setTotalPages(response.data.totalPages || 1);
    } catch (error) {
      console.error('Error fetching details:', error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch categories, brands, and features
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/categories');
        setCategories(response.data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    const fetchBrands = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/brands');
        setBrands(response.data);
      } catch (error) {
        console.error('Error fetching brands:', error);
      }
    };

    const fetchFeatures = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/features');
        setFeatures(response.data);
      } catch (error) {
        console.error('Error fetching features:', error);
      }
    };

    fetchCategories();
    fetchBrands();
    fetchFeatures();
  }, []);

  // Fetch details whenever filters, sorting, or page changes
  useEffect(() => {
    fetchData();
  }, [selectedFilters, sortOption, page]);

  const handleFilterChange = (filterName, value) => {
    setSelectedFilters(prevFilters => ({
      ...prevFilters,
      [filterName]: value
    }));
  };

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
  };

  const handlePagination = (direction) => {
    setPage(prevPage => {
      const newPage = direction === 'next' ? prevPage + 1 : prevPage - 1;
      return (newPage >= 1 && newPage <= totalPages) ? newPage : prevPage;
    });
  };

  if (loading) {
    return (
      <div className="text-center p-8">
        <div className="spinner-border animate-spin inline-block w-12 h-12 border-4 border-current border-t-transparent text-blue-500 rounded-full" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-wrap lg:flex-nowrap">
      <aside className="w-full lg:w-1/4 p-4 bg-white shadow-md rounded-lg lg:mr-4">
        <h2 className="text-xl font-semibold mb-4">Filters</h2>

        <div className="mb-6">
          <h3 className="font-semibold mb-2">Category</h3>
          <select
            value={selectedFilters.category}
            onChange={(e) => handleFilterChange('category', e.target.value)}
            className="block w-full bg-white border border-gray-400 px-4 py-2 rounded"
          >
            <option value="">All Categories</option>
            {categories.map(category => (
              <option key={category._id} value={category.name}>{category.name}</option>
            ))}
          </select>
        </div>

        <div className="mb-6">
          <h3 className="font-semibold mb-2">Price Range</h3>
          <input
            type="range"
            min="0"
            max="15000"
            value={selectedFilters.priceMin}
            onChange={(e) => handleFilterChange('priceMin', Number(e.target.value))}
            className="w-full mb-2"
          />
          <input
            type="range"
            min="0"
            max="15000"
            value={selectedFilters.priceMax}
            onChange={(e) => handleFilterChange('priceMax', Number(e.target.value))}
            className="w-full"
          />
          <div className="flex justify-between text-xs mt-2">
            <span>${selectedFilters.priceMin}</span>
            <span>${selectedFilters.priceMax}</span>
          </div>
        </div>

        <div className="mb-6">
          <h3 className="font-semibold mb-2">Availability</h3>
          <ul className="space-y-2">
            <li>
              <input
                type="checkbox"
                value="inStock"
                checked={selectedFilters.availability.includes('inStock')}
                onChange={(e) => {
                  const value = e.target.value;
                  setSelectedFilters(prevFilters => ({
                    ...prevFilters,
                    availability: prevFilters.availability.includes(value)
                      ? prevFilters.availability.filter(v => v !== value)
                      : [...prevFilters.availability, value]
                  }));
                }}
                className="mr-2"
              />
              <label>In Stock</label>
            </li>
            <li>
              <input
                type="checkbox"
                value="outOfStock"
                checked={selectedFilters.availability.includes('outOfStock')}
                onChange={(e) => {
                  const value = e.target.value;
                  setSelectedFilters(prevFilters => ({
                    ...prevFilters,
                    availability: prevFilters.availability.includes(value)
                      ? prevFilters.availability.filter(v => v !== value)
                      : [...prevFilters.availability, value]
                  }));
                }}
                className="mr-2"
              />
              <label>Out of Stock</label>
            </li>
          </ul>
        </div>

        <div className="mb-6">
          <h3 className="font-semibold mb-2">Brand</h3>
          <ul className="space-y-2">
            {brands.map(brand => (
              <li key={brand._id}>
                <input
                  type="checkbox"
                  value={brand.name}
                  checked={selectedFilters.brands.includes(brand.name)}
                  onChange={(e) => {
                    const value = e.target.value;
                    setSelectedFilters(prevFilters => ({
                      ...prevFilters,
                      brands: prevFilters.brands.includes(value)
                        ? prevFilters.brands.filter(v => v !== value)
                        : [...prevFilters.brands, value]
                    }));
                  }}
                  className="mr-2"
                />
                <label>{brand.name}</label>
              </li>
            ))}
          </ul>
        </div>

        <div className="mb-6">
          <h3 className="font-semibold mb-2">Features</h3>
          <ul className="space-y-2">
            {features.map(feature => (
              <li key={feature._id}>
                <input
                  type="checkbox"
                  value={feature.name}
                  checked={selectedFilters.features.includes(feature.name)}
                  onChange={(e) => {
                    const value = e.target.value;
                    setSelectedFilters(prevFilters => ({
                      ...prevFilters,
                      features: prevFilters.features.includes(value)
                        ? prevFilters.features.filter(v => v !== value)
                        : [...prevFilters.features, value]
                    }));
                  }}
                  className="mr-2"
                />
                <label>{feature.name}</label>
              </li>
            ))}
          </ul>
        </div>
      </aside>

      <main className="w-full lg:w-3/4 p-4">
        <div className="flex justify-between items-center mb-6">
          <div>
            <label htmlFor="sort" className="font-semibold text-gray-700">Sort by:</label>
            <select
              id="sort"
              value={sortOption}
              onChange={handleSortChange}
              className="ml-2 p-2 border rounded bg-white shadow-sm"
            >
              <option value="popularity">Popularity</option>
              <option value="price-low-high">Price: Low to High</option>
              <option value="price-high-low">Price: High to Low</option>
              <option value="newest">Newest</option>
            </select>
          </div>
          <button
            onClick={fetchData}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors duration-300"
          >
            Apply Filters
          </button>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {details.length > 0 ? (
            details.map(detail => (
              <div key={detail._id} className="bg-white border border-gray-300 rounded-lg shadow-md p-4">
                <img src={detail.imageUrl || 'default-image-url'} alt={detail.name} className="w-full h-32 object-cover mb-4 rounded-md" />
                <h3 className="text-lg font-semibold mb-2">{detail.name}</h3>
                <p className="text-gray-700 mb-2">Category: {detail.category}</p>
                <p className="text-gray-700 mb-2">Price: ${detail.price}</p>
                <p className="text-gray-700 mb-2">Brand: {detail.brand}</p>
                <p className="text-gray-700 mb-4">Availability: {detail.availability}</p>
                <Link to={`/details/${detail._id}`} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors duration-300">
                  View Details
                </Link>
              </div>
            ))
          ) : (
            <p>No details found.</p>
          )}
        </div>

        <div className="flex justify-between items-center mt-6">
          <button
            onClick={() => handlePagination('prev')}
            disabled={page === 1}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors duration-300"
          >
            Previous
          </button>
          <span>Page {page} of {totalPages}</span>
          <button
            onClick={() => handlePagination('next')}
            disabled={page === totalPages}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors duration-300"
          >
            Next
          </button>
        </div>
      </main>
    </div>
  );
}

export default DetailCards;
