import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory, useParams } from 'react-router-dom';

function CreateDetail() {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    machine: '',
    imageUrl: ''
  });
  const { name, description, price, machine, imageUrl } = formData;
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const history = useHistory();
  const { id } = useParams();

  useEffect(() => {
    if (id) {
      const fetchDetail = async () => {
        try {
          const res = await axios.get(`http://localhost:3001/api/details/${id}`);
          setFormData(res.data);
          setIsEditing(true);
        } catch (err) {
          console.error('Fetch Detail error:', err);
          setError('Failed to fetch detail');
        }
      };
      fetchDetail();
    }
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    if (!name || !description || !price || !machine || !imageUrl) {
      setError('All fields are required');
      return;
    }
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };
      const url = isEditing
        ? `http://localhost:3001/api/details/${id}`
        : 'http://localhost:3001/api/details';

      const method = isEditing ? 'put' : 'post';

      await axios[method](url, formData, { headers });
      history.push('/');
      alert(`${isEditing ? 'Detail updated' : 'Detail created'}`);
    } catch (err) {
      console.error(isEditing ? 'Detail update error:' : 'Detail creation error:', err);
      setError(err.response?.data?.message || 'Failed to save detail');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-8 bg-gray-50 shadow-xl rounded-lg mt-12 max-w-lg">
      <h1 className="text-4xl font-extrabold mb-8 text-center text-gray-800">{isEditing ? 'Edit Detail' : 'Create Detail'}</h1>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-lg text-gray-700 font-medium mb-2" htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            name="name"
            placeholder="Enter product name"
            value={name}
            onChange={handleChange}
            className="w-full border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-300"
            aria-required="true"
            aria-invalid={error && !name ? "true" : "false"}
          />
        </div>
        <div>
          <label className="block text-lg text-gray-700 font-medium mb-2" htmlFor="machine">Machine</label>
          <input
            type="text"
            id="machine"
            name="machine"
            placeholder="Enter machine name"
            value={machine}
            onChange={handleChange}
            className="w-full border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-300"
            aria-required="true"
            aria-invalid={error && !machine ? "true" : "false"}
          />
        </div>
        <div>
          <label className="block text-lg text-gray-700 font-medium mb-2" htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            placeholder="Enter product description"
            value={description}
            onChange={handleChange}
            className="w-full border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-300 h-32 resize-none"
            aria-required="true"
            aria-invalid={error && !description ? "true" : "false"}
          />
        </div>
        <div>
          <label className="block text-lg text-gray-700 font-medium mb-2" htmlFor="price">Price</label>
          <input
            type="number"
            id="price"
            name="price"
            placeholder="Enter product price"
            value={price}
            onChange={handleChange}
            className="w-full border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-300"
            aria-required="true"
            aria-invalid={error && !price ? "true" : "false"}
          />
        </div>
        <div>
          <label className="block text-lg text-gray-700 font-medium mb-2" htmlFor="imageUrl">Image URL</label>
          <input
            type="text"
            id="imageUrl"
            name="imageUrl"
            placeholder="Enter image URL"
            value={imageUrl}
            onChange={handleChange}
            className="w-full border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-300"
            aria-required="true"
            aria-invalid={error && !imageUrl ? "true" : "false"}
          />
        </div>
        <div className="text-center mt-8">
          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 px-6 rounded-full bg-blue-500 text-white text-lg font-semibold hover:bg-gray-600 transition duration-300 shadow-lg transform hover:scale-105 ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
            aria-busy={loading ? "true" : "false"}
          >
            {isEditing ? 'Update Detail' : 'Create Detail'}
          </button>
          {error && <div className="text-red-500 mt-4">{error}</div>}
        </div>
      </form>
    </div>
  );
}

export default CreateDetail;
