import React, { useEffect, useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import axios from 'axios';
import Hero from './Hero';

function DishCards() {
  const [dishes, setDishes] = useState([]);
  const [loading, setLoading] = useState(true);
  const location = useLocation();
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  useEffect(() => {
    const fetchDishes = async () => {
      setLoading(true);
      try {
        const res = await axios.get('http://localhost:3001/api/dishes');
        setDishes(res.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchDishes();
  }, [location]);

  const addToCart = async (dishId) => {
    try {
      const response = await axios.post('http://localhost:3001/api/cart', { dishId }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Dish added to cart');
    } catch (err) {
      console.error('Add to cart error:', err);
      alert('Failed to add to cart');
    }
  };

  const deleteDish = async (dishId) => {
    try {
      await axios.delete(`http://localhost:3001/api/dishes/${dishId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDishes(dishes.filter(dish => dish._id !== dishId));
      alert('Dish deleted');
    } catch (err) {
      console.error('Delete dish error:', err);
      alert('Failed to delete dish');
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <Hero />
      <div className="container mx-auto px-4 py-8" id="Dishes">
        <h1 className="text-3xl font-bold mb-6 text-center text-gray-800">Dishes</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {dishes.map(dish => (
            <div key={dish._id} className="border border-gray-200 p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300">
              {dish.imageUrl && (
                <img 
                  src={dish.imageUrl} 
                  alt={dish.name} 
                  className="w-full h-64 object-cover mb-4 rounded-t-lg" 
                />
              )}
              <div className="p-4">
                <h2 className="text-xl font-semibold text-gray-800 mb-2">{dish.name}</h2>
                <p className="text-gray-600 mb-4">{dish.description}</p>
                <p className="text-gray-700 font-bold mb-4">${dish.price}</p>
                {role === 'admin' ? (
                  <div className="flex justify-between items-center">
                    <Link 
                      to={`/create-dish/${dish._id}`} 
                      className="text-blue-500 hover:underline"
                    >
                      Edit
                    </Link>
                    <button
                      onClick={() => deleteDish(dish._id)}
                      className="bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600 transition-colors duration-200"
                    >
                      Delete
                    </button>
                  </div>
                ) : (
                  <button
                    onClick={() => addToCart(dish._id)}
                    className="w-full bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 transition-colors duration-200"
                  >
                    Add to Cart
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default DishCards;
