import React from 'react';
import { Link } from 'react-router-dom';

function ProductCard({ detail, addToCart }) {
  return (
    <div key={detail._id} className="bg-white border border-gray-200 rounded-2xl shadow-xl transition-transform transform hover:scale-105 hover:shadow-2xl p-6 hover:bg-gradient-to-r hover:from-blue-50 hover:to-blue-100">
      <div className="relative overflow-hidden rounded-xl">
        <img
          src={detail.imageUrl || 'default-image-url'}
          alt={detail.name}
          className="w-full h-56 object-cover rounded-t-xl transition-transform duration-500 hover:scale-110"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black to-transparent opacity-0 hover:opacity-60 transition-opacity duration-300"></div>
        <h3 className="absolute bottom-0 left-0 right-0 text-white text-lg font-bold p-4 transition-opacity duration-300 opacity-0 hover:opacity-100">{detail.name}</h3>
      </div>
      <div className="p-6">
        <p className="text-gray-700 mb-2">Category: <span className="font-semibold text-gray-900">{detail.category}</span></p>
        <p className="text-gray-700 mb-2">Price: <span className="font-semibold text-gray-900">${detail.price}</span></p>
        <p className="text-gray-700 mb-2">Brand: <span className="font-semibold text-gray-900">{detail.brand}</span></p>
        <p className="text-gray-700 mb-6">Availability: <span className="font-semibold text-gray-900">{detail.availability}</span></p>
        <div className="flex space-x-4">
          <Link
            to={`/details/${detail._id}`}
            className="flex-1 text-center bg-gradient-to-r from-blue-500 to-purple-600 text-white px-4 py-2 rounded-lg hover:from-blue-600 hover:to-purple-700 transition-all duration-300 shadow-md hover:shadow-lg"
          >
            View Details
          </Link>
          <button
            onClick={() => addToCart(detail._id)}
            className="flex-1 text-center bg-gradient-to-r from-green-500 to-teal-600 text-white px-4 py-2 rounded-lg hover:from-green-600 hover:to-teal-700 transition-all duration-300 shadow-md hover:shadow-lg"
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}

export default ProductCard;
