import React from 'react';

function Categories() {
  const categories = [
    { name: 'VR', imgSrc: 'https://images.unsplash.com/photo-1593508512255-86ab42a8e620?auto=format&q=75&fit=crop&w=600' },
    { name: 'Gaming', imgSrc: 'https://images.unsplash.com/photo-1593508512255-86ab42a8e620?auto=format&q=75&fit=crop&w=600' },
    { name: 'Electronics', imgSrc: 'https://images.unsplash.com/photo-1593508512255-86ab42a8e620?auto=format&q=75&fit=crop&w=600' },
    { name: 'Home Appliances', imgSrc: 'https://images.unsplash.com/photo-1593508512255-86ab42a8e620?auto=format&q=75&fit=crop&w=600' },
    // Add more categories as needed
  ];

  return (
    <div className="bg-white py-8">
      <div className="container mx-auto px-4 md:px-8">
        <div className="mb-8 flex items-center justify-between">
          <h2 className="text-3xl font-bold text-gray-800">Categories</h2>
          <a href="#" className="inline-block rounded-lg border bg-white px-4 py-2 text-center text-sm font-semibold text-gray-500 outline-none ring-indigo-300 transition duration-150 hover:bg-gray-100 focus:ring active:bg-gray-200">
            More
          </a>
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {categories.map((category, index) => (
            <a
              key={index}
              href="#"
              className="group relative block h-64 overflow-hidden rounded-lg bg-gray-100 shadow-lg transition-transform transform hover:scale-105"
            >
              <img
                src={category.imgSrc}
                alt={category.name}
                className="absolute inset-0 w-full h-full object-cover object-center transition-transform group-hover:scale-110"
              />
              <div className="absolute inset-0 bg-gradient-to-t from-gray-800 via-transparent to-transparent opacity-50"></div>
              <span className="relative text-white text-xl font-semibold mb-3 ml-4">{category.name}</span>
            </a>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Categories;
