import React, { useState } from 'react';

function FilterSidebar({ categories, brands, features, selectedFilters, onFilterChange, sortOption }) {
  const [expandedSections, setExpandedSections] = useState({});

  const toggleSection = (section) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  return (
    <aside className="w-full lg:w-1/4 p-4 bg-white shadow-lg rounded-lg mb-6 lg:mb-0 lg:mr-6">
      <h2 className="text-xl font-bold text-gray-800 mb-4">Filter</h2>

      <FilterSection 
        title="Category" 
        isExpanded={expandedSections.category}
        onToggle={() => toggleSection('category')}
      >
        <select
          value={selectedFilters.category}
          onChange={(e) => onFilterChange('category', e.target.value)}
          className="block w-full border border-gray-300 rounded p-2 mb-4"
        >
          <option value="">All Categories</option>
          {categories.map(category => (
            <option key={category._id} value={category.name}>{category.name}</option>
          ))}
        </select>
      </FilterSection>

      <FilterSection 
        title="Price Range" 
        isExpanded={expandedSections.price}
        onToggle={() => toggleSection('price')}
      >
        <div className="flex justify-between text-sm text-gray-700 mb-4">
          <span>${selectedFilters.priceMin}</span>
          <span>${selectedFilters.priceMax}</span>
        </div>
        <div className="flex space-x-4">
          <input
            type="range"
            min="0"
            max="15000"
            value={selectedFilters.priceMin}
            onChange={(e) => onFilterChange('priceMin', Number(e.target.value))}
            className="w-full"
          />
          <input
            type="range"
            min="0"
            max="15000"
            value={selectedFilters.priceMax}
            onChange={(e) => onFilterChange('priceMax', Number(e.target.value))}
            className="w-full"
          />
        </div>
      </FilterSection>

      <FilterSection 
        title="Availability" 
        isExpanded={expandedSections.availability}
        onToggle={() => toggleSection('availability')}
      >
        <Checkbox
          label="In Stock"
          value="inStock"
          checked={selectedFilters.availability.includes('inStock')}
          onChange={(e) => onFilterChange('availability', e.target.checked
            ? [...selectedFilters.availability, e.target.value]
            : selectedFilters.availability.filter(avail => avail !== e.target.value)
          )}
        />
      </FilterSection>

      <FilterSection 
        title="Brands" 
        isExpanded={expandedSections.brands}
        onToggle={() => toggleSection('brands')}
      >
        {brands.map(brand => (
          <Checkbox
            key={brand._id}
            label={brand.name}
            value={brand.name}
            checked={selectedFilters.brands.includes(brand.name)}
            onChange={(e) => onFilterChange('brands', e.target.checked
              ? [...selectedFilters.brands, e.target.value]
              : selectedFilters.brands.filter(b => b !== e.target.value)
            )}
          />
        ))}
      </FilterSection>

      <FilterSection 
        title="Features" 
        isExpanded={expandedSections.features}
        onToggle={() => toggleSection('features')}
      >
        {features.map(feature => (
          <Checkbox
            key={feature._id}
            label={feature.name}
            value={feature.name}
            checked={selectedFilters.features.includes(feature.name)}
            onChange={(e) => onFilterChange('features', e.target.checked
              ? [...selectedFilters.features, e.target.value]
              : selectedFilters.features.filter(f => f !== e.target.value)
            )}
          />
        ))}
      </FilterSection>
    </aside>
  );
}

function FilterSection({ title, isExpanded, onToggle, children }) {
  return (
    <div className="mb-6">
      <button 
        onClick={onToggle}
        className="w-full flex justify-between items-center text-gray-700 font-semibold mb-2"
      >
        {title}
        <svg
          className={`w-4 h-4 transform transition-transform duration-300 ${isExpanded ? 'rotate-180' : 'rotate-0'}`}
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
        </svg>
      </button>
      {isExpanded && <div>{children}</div>}
    </div>
  );
}

function Checkbox({ label, value, checked, onChange }) {
  return (
    <label className="inline-flex items-center mt-2">
      <input
        type="checkbox"
        value={value}
        checked={checked}
        onChange={onChange}
        className="form-checkbox h-4 w-4 text-blue-600"
      />
      <span className="ml-2 text-gray-700">{label}</span>
    </label>
  );
}

export default FilterSidebar;
