import React from 'react';
import { Link } from 'react-router-dom';

function ProductCard({ detail, addToCart }) {
  return (
    <div key={detail._id} className="bg-white border border-gray-200 rounded-xl shadow-lg transition-transform transform hover:scale-105 hover:shadow-2xl p-6">
      <img
        src={detail.imageUrl || 'default-image-url'}
        alt={detail.name}
        className="w-full h-52 object-cover rounded-t-xl mb-6 transition-opacity duration-300 hover:opacity-90"
      />
      <div className="p-4">
        <h3 className="text-2xl font-bold mb-3 text-gray-900">{detail.name}</h3>
        <p className="text-gray-700 mb-2">Category: <span className="font-semibold text-gray-800">{detail.category}</span></p>
        <p className="text-gray-700 mb-2">Price: <span className="font-semibold text-gray-800">${detail.price}</span></p>
        <p className="text-gray-700 mb-2">Brand: <span className="font-semibold text-gray-800">{detail.brand}</span></p>
        <p className="text-gray-700 mb-6">Availability: <span className="font-semibold text-gray-800">{detail.availability}</span></p>
        <div className="flex space-x-4">
          <Link
            to={`/details/${detail._id}`}
            className="flex-1 text-center bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors duration-300 shadow-md hover:shadow-lg"
          >
            View Details
          </Link>
          <button
            onClick={() => addToCart(detail._id)}
            className="flex-1 text-center bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition-colors duration-300 shadow-md hover:shadow-lg"
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}

export default ProductCard;
