import React, { useState } from 'react';
import PropTypes from 'prop-types';

function FilterSidebar({ machines, selectedFilters, onFilterChange }) {
  const [expandedSections, setExpandedSections] = useState({});

  const toggleSection = (section) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  return (
    <aside className="w-full lg:w-1/4 p-4 bg-white shadow-lg rounded-lg mb-6 lg:mb-0 lg:mr-6">
      <h2 className="text-xl font-bold text-gray-800 mb-4">Filter</h2>

      {/* {machines.map(machine => (
        <FilterSection
          key={machine.id}
          title={machine.name}
          isExpanded={expandedSections[machine.id]}
          onToggle={() => toggleSection(machine.id)}
        >
          <FilterFields
            fields={machine.fields}
            selectedFilters={selectedFilters}
            onFilterChange={onFilterChange}
          />
        </FilterSection>
      ))} */}
    </aside>
  );
}

FilterSidebar.propTypes = {
  machines: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
      fields: PropTypes.arrayOf(
        PropTypes.shape({
          id: PropTypes.string.isRequired,
          name: PropTypes.string.isRequired,
          type: PropTypes.string.isRequired
        })
      ).isRequired
    })
  ).isRequired,
  selectedFilters: PropTypes.object.isRequired,
  onFilterChange: PropTypes.func.isRequired
};

function FilterSection({ title, isExpanded, onToggle, children }) {
  return (
    <div className="mb-6">
      <button 
        onClick={onToggle}
        className="w-full flex justify-between items-center text-gray-700 font-semibold mb-2"
      >
        {title}
        <svg
          className={`w-4 h-4 transform transition-transform duration-300 ${isExpanded ? 'rotate-180' : 'rotate-0'}`}
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
        </svg>
      </button>
      {isExpanded && <div>{children}</div>}
    </div>
  );
}

function FilterFields({ fields, selectedFilters, onFilterChange }) {
  return (
    <div>
      {fields.map(field => (
        <div key={field.id} className="mb-4">
          {field.type === 'checkbox' && (
            <Checkbox
              label={field.name}
              value={field.name}
              checked={selectedFilters[field.name] || false}
              onChange={(e) => onFilterChange(field.name, e.target.checked)}
            />
          )}
          {/* Add other field types as needed */}
        </div>
      ))}
    </div>
  );
}

function Checkbox({ label, value, checked, onChange }) {
  return (
    <label className="inline-flex items-center mt-2">
      <input
        type="checkbox"
        value={value}
        checked={checked}
        onChange={onChange}
        className="form-checkbox h-4 w-4 text-blue-600"
      />
      <span className="ml-2 text-gray-700">{label}</span>
    </label>
  );
}

export default FilterSidebar;
