import React, { useState } from 'react';

function FilterSidebar({ machines, selectedFilters, onFilterChange }) {
  const [expandedSections, setExpandedSections] = useState({});

  const toggleSection = (machineId) => {
    setExpandedSections(prev => ({
      ...prev,
      [machineId]: !prev[machineId]
    }));
  };

  return (
    <aside className="w-full lg:w-1/4 p-4 bg-white shadow-lg rounded-lg mb-6 lg:mb-0 lg:mr-6">
      <h2 className="text-xl font-bold text-gray-800 mb-4">Filter</h2>

      {machines.map(machine => (
        <FilterSection
          key={machine.id}
          title={machine.name}
          isExpanded={expandedSections[machine.id]}
          onToggle={() => toggleSection(machine.id)}
        >
          {machine.filters.map(filter => (
            <FilterItem
              key={filter.name}
              filter={filter}
              selectedFilters={selectedFilters[machine.id] || {}}
              onFilterChange={(value) => onFilterChange(machine.id, filter.name, value)}
            />
          ))}
        </FilterSection>
      ))}
    </aside>
  );
}

function FilterSection({ title, isExpanded, onToggle, children }) {
  return (
    <div className="mb-6">
      <button 
        onClick={onToggle}
        className="w-full flex justify-between items-center text-gray-700 font-semibold mb-2"
      >
        {title}
        <svg
          className={`w-4 h-4 transform transition-transform duration-300 ${isExpanded ? 'rotate-180' : 'rotate-0'}`}
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
        </svg>
      </button>
      {isExpanded && <div>{children}</div>}
    </div>
  );
}

function FilterItem({ filter, selectedFilters, onFilterChange }) {
  if (filter.type === 'select') {
    return (
      <div className="mb-4">
        <label className="block font-semibold text-gray-700 mb-2">{filter.label}</label>
        <select
          value={selectedFilters[filter.name] || ''}
          onChange={(e) => onFilterChange(e.target.value)}
          className="block w-full border border-gray-300 rounded p-2"
        >
          <option value="">All {filter.label}</option>
          {filter.options.map(option => (
            <option key={option.value} value={option.value}>{option.label}</option>
          ))}
        </select>
      </div>
    );
  }

  if (filter.type === 'range') {
    return (
      <div className="mb-4">
        <label className="block font-semibold text-gray-700 mb-2">{filter.label}</label>
        <div className="flex justify-between text-sm text-gray-700 mb-2">
          <span>${selectedFilters[filter.name]?.min || 0}</span>
          <span>${selectedFilters[filter.name]?.max || 10000}</span>
        </div>
        <input
          type="range"
          min="0"
          max="10000"
          value={selectedFilters[filter.name]?.min || 0}
          onChange={(e) => onFilterChange({ min: Number(e.target.value), max: selectedFilters[filter.name]?.max || 10000 })}
          className="w-full"
        />
        <input
          type="range"
          min="0"
          max="10000"
          value={selectedFilters[filter.name]?.max || 10000}
          onChange={(e) => onFilterChange({ min: selectedFilters[filter.name]?.min || 0, max: Number(e.target.value) })}
          className="w-full mt-2"
        />
      </div>
    );
  }

  if (filter.type === 'checkbox') {
    return (
      <div className="mb-4">
        {filter.options.map(option => (
          <label key={option.value} className="inline-flex items-center mt-2">
            <input
              type="checkbox"
              value={option.value}
              checked={selectedFilters[filter.name]?.includes(option.value) || false}
              onChange={(e) => onFilterChange(e.target.checked
                ? [...(selectedFilters[filter.name] || []), option.value]
                : (selectedFilters[filter.name] || []).filter(v => v !== option.value)
              )}
              className="form-checkbox h-4 w-4 text-blue-600"
            />
            <span className="ml-2 text-gray-700">{option.label}</span>
          </label>
        ))}
      </div>
    );
  }

  return null;
}

export default FilterSidebar;
