import React, { useEffect, useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import axios from 'axios';
import Hero from './Hero';

function PartCards() {
  const [parts, setParts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const location = useLocation();
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  useEffect(() => {
    const fetchParts = async () => {
      setLoading(true);
      try {
        const res = await axios.get('http://localhost:3001/api/parts');
        setParts(res.data);
      } catch (err) {
        console.error('Error fetching parts:', err);
        // Optionally set an error state here
      } finally {
        setLoading(false);
      }
    };

    const fetchCategories = async () => {
      try {
        const res = await axios.get('http://localhost:3001/api/categories');
        setCategories(res.data);
      } catch (err) {
        console.error('Error fetching categories:', err);
        // Optionally set an error state here
      }
    };

    fetchParts();
    fetchCategories();
  }, [location]);

  const addToCart = async (partId) => {
    try {
      await axios.post('http://localhost:3001/api/cart', { partId }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Part added to cart');
    } catch (err) {
      console.error('Error adding part to cart:', err);
      alert('Failed to add to cart');
    }
  };

  const deletePart = async (partId) => {
    try {
      await axios.delete(`http://localhost:3001/api/parts/${partId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setParts(prevParts => prevParts.filter(part => part._id !== partId));
      alert('Part deleted');
    } catch (err) {
      console.error('Error deleting part:', err);
      alert('Failed to delete part');
    }
  };

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
  };

  const filteredParts = selectedCategory
    ? parts.filter(part => part.category === selectedCategory)
    : parts;

  if (loading) {
    return (
      <div className="text-center p-8">
        <div className="spinner-border animate-spin inline-block w-12 h-12 border-4 border-current border-t-transparent text-blue-500 rounded-full" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Hero />
      <div className="container mx-auto px-4 py-8" id="Parts">
        <h1 className="text-3xl font-bold mb-6 text-center text-gray-800">Auto Parts</h1>
        <div className="mb-4">
          <label htmlFor="category" className="block text-gray-700 font-bold mb-2">Filter by Category:</label>
          <select
            id="category"
            value={selectedCategory}
            onChange={handleCategoryChange}
            className="block appearance-none w-full bg-white border border-gray-400 hover:border-gray-500 px-4 py-2 pr-8 rounded shadow leading-tight focus:outline-none focus:shadow-outline"
          >
            <option value="">All Categories</option>
            {categories.map(category => (
              <option key={category._id} value={category.name}>{category.name}</option>
            ))}
          </select>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredParts.length > 0 ? (
            filteredParts.map(part => (
              <div key={part._id} className="border border-gray-200 p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300">
                {part.imageUrl && (
                  <img 
                    src={part.imageUrl} 
                    alt={part.name} 
                    className="w-full h-64 object-cover mb-4 rounded-t-lg" 
                  />
                )}
                <div className="p-4">
                  <h2 className="text-xl font-semibold text-gray-800 mb-2">{part.name}</h2>
                  <p className="text-gray-600 mb-4">{part.description}</p>
                  <p className="text-gray-700 font-bold mb-4">${part.price}</p>
                  {role === 'admin' ? (
                    <div className="flex justify-between items-center">
                      <Link 
                        to={`/edit-part/${part._id}`} 
                        className="text-blue-500 hover:underline"
                      >
                        Edit
                      </Link>
                      <button
                        onClick={() => deletePart(part._id)}
                        className="bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600 transition-colors duration-200"
                      >
                        Delete
                      </button>
                    </div>
                  ) : (
                    <button
                      onClick={() => addToCart(part._id)}
                      className="w-full bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 transition-colors duration-200"
                    >
                      Add to Cart
                    </button>
                  )}
                </div>
              </div>
            ))
          ) : (
            <div className="text-center p-8">No parts available for the selected category.</div>
          )}
        </div>
      </div>
    </div>
  );
}

export default PartCards;
