import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory, useParams } from 'react-router-dom';

function CreateDetail() {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    machine: '',
    imageUrls: [''] // Start with one empty image URL field
  });
  const [machines, setMachines] = useState([]);
  const [selectedMachine, setSelectedMachine] = useState(null);
  const { name, description, price, machine, imageUrls } = formData;
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const history = useHistory();
  const { id } = useParams();

  useEffect(() => {
    const fetchMachines = async () => {
      try {
        const res = await axios.get('http://localhost:3001/api/machines');
        setMachines(res.data);
      } catch (err) {
        console.error('Fetch machines error:', err);
        setError('Failed to fetch machines');
      }
    };
    fetchMachines();

    if (id) {
      const fetchDetail = async () => {
        try {
          const res = await axios.get(`http://localhost:3001/api/details/${id}`);
          setFormData({ ...res.data, imageUrls: res.data.imageUrls || [''] });
          setIsEditing(true);
        } catch (err) {
          console.error('Fetch Detail error:', err);
          setError('Failed to fetch detail');
        }
      };
      fetchDetail();
    }
  }, [id]);

  useEffect(() => {
    const machine = machines.find(m => m._id === formData.machine);
    setSelectedMachine(machine || null);
  }, [formData.machine, machines]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleImageUrlChange = (index, value) => {
    const newImageUrls = [...imageUrls];
    newImageUrls[index] = value;
    setFormData((prevData) => ({
      ...prevData,
      imageUrls: newImageUrls,
    }));
  };

  const addImageUrlField = () => {
    setFormData((prevData) => ({
      ...prevData,
      imageUrls: [...prevData.imageUrls, ''],
    }));
  };

  const removeImageUrlField = (index) => {
    setFormData((prevData) => ({
      ...prevData,
      imageUrls: prevData.imageUrls.filter((_, i) => i !== index),
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    if (!name || !description || !price || !machine || imageUrls.some(url => !url)) {
      setError('All fields are required');
      return;
    }
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };
      const url = isEditing
        ? `http://localhost:3001/api/details/${id}`
        : 'http://localhost:3001/api/details';

      const method = isEditing ? 'put' : 'post';

      await axios[method](url, { ...formData, imageUrls }, { headers });
      history.push('/');
      alert(`${isEditing ? 'Detail updated' : 'Detail created'}`);
    } catch (err) {
      console.error(isEditing ? 'Detail update error:' : 'Detail creation error:', err);
      setError(err.response?.data?.message || 'Failed to save detail');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-6 bg-white shadow-lg rounded-xl mt-12 max-w-lg border border-gray-200">
      <h1 className="text-4xl font-bold mb-8 text-center text-gray-800">{isEditing ? 'Edit Detail' : 'Create Detail'}</h1>
      <form onSubmit={handleSubmit} className="space-y-6">
        {[
          { id: 'name', label: 'Name', type: 'text', value: name, placeholder: 'Enter product name' },
          { id: 'machine', label: 'Machine', type: 'select', value: machine, placeholder: 'Select machine' },
          { id: 'description', label: 'Description', type: 'textarea', value: description, placeholder: 'Enter product description', className: 'h-36 resize-none' },
          { id: 'price', label: 'Price', type: 'number', value: price, placeholder: 'Enter product price' }
        ].map(({ id, label, type, value, placeholder, className }) => (
          <div key={id} className="flex flex-col gap-2">
            <label className="text-lg text-gray-700 font-semibold" htmlFor={id}>{label}</label>
            {type === 'select' ? (
              <select
                id={id}
                name={id}
                value={value}
                onChange={handleChange}
                className="border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-300"
              >
                <option value="" disabled>Select machine</option>
                {machines.map(machine => (
                  <option key={machine._id} value={machine._id}>{machine.name}</option>
                ))}
              </select>
            ) : type === 'textarea' ? (
              <textarea
                id={id}
                name={id}
                placeholder={placeholder}
                value={value}
                onChange={handleChange}
                className={`border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-300 ${className}`}
                aria-required="true"
                aria-invalid={error && !value ? "true" : "false"}
              />
            ) : (
              <input
                type={type}
                id={id}
                name={id}
                placeholder={placeholder}
                value={value}
                onChange={handleChange}
                className="border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-300"
                aria-required="true"
                aria-invalid={error && !value ? "true" : "false"}
              />
            )}
          </div>
        ))}
        {selectedMachine && (
          <div className="flex flex-col gap-2">
            <label className="text-lg text-gray-700 font-semibold">Machine Image</label>
            <img src={selectedMachine.imageUrl} alt={selectedMachine.name} className="w-full h-auto rounded-lg" />
          </div>
        )}
        <div className="flex flex-col gap-2">
          <label className="text-lg text-gray-700 font-semibold">Image URLs</label>
          {imageUrls.map((url, index) => (
            <div key={index} className="flex items-center gap-2">
              <input
                type="text"
                value={url}
                onChange={(e) => handleImageUrlChange(index, e.target.value)}
                placeholder="Enter image URL"
                className="border border-gray-300 p-3 rounded-lg flex-1"
              />
              <button
                type="button"
                onClick={() => removeImageUrlField(index)}
                className="text-red-500 hover:text-red-700"
              >
                Remove
              </button>
            </div>
          ))}
          <button
            type="button"
            onClick={addImageUrlField}
            className="mt-2 py-2 px-4 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700"
          >
            Add Another Image URL
          </button>
        </div>
        <div className="text-center mt-8">
          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 px-6 rounded-lg bg-indigo-600 text-white text-lg font-semibold hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-300 shadow-md ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
            aria-busy={loading ? "true" : "false"}
          >
            {isEditing ? 'Update Detail' : 'Create Detail'}
          </button>
          {error && <div className="text-red-500 mt-4 text-center text-sm">{error}</div>}
        </div>
      </form>
    </div>
  );
}

export default CreateDetail;
