import React, { useEffect, useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import axios from 'axios';
import Hero from './Hero';

function PartCards() {
  const [parts, setParts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [priceRange, setPriceRange] = useState({ min: 0, max: 100 });
  const [availability, setAvailability] = useState([]);
  const [brands, setBrands] = useState([]);
  const [features, setFeatures] = useState([]);
  const [selectedFilters, setSelectedFilters] = useState({
    category: '',
    priceMin: 0,
    priceMax: 100,
    availability: [],
    brands: [],
    features: []
  });
  const location = useLocation();
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  useEffect(() => {
    const fetchParts = async () => {
      setLoading(true);
      try {
        const res = await axios.get('http://localhost:3001/api/parts', { params: selectedFilters });
        setParts(res.data);
      } catch (err) {
        console.error('Error fetching parts:', err);
      } finally {
        setLoading(false);
      }
    };

    const fetchCategories = async () => {
      try {
        const res = await axios.get('http://localhost:3001/api/categories');
        setCategories(res.data);
      } catch (err) {
        console.error('Error fetching categories:', err);
      }
    };

    const fetchBrands = async () => {
      try {
        const res = await axios.get('http://localhost:3001/api/brands');
        setBrands(res.data);
      } catch (err) {
        console.error('Error fetching brands:', err);
      }
    };

    const fetchFeatures = async () => {
      try {
        const res = await axios.get('http://localhost:3001/api/features');
        setFeatures(res.data);
      } catch (err) {
        console.error('Error fetching features:', err);
      }
    };

    fetchParts();
    fetchCategories();
    fetchBrands();
    fetchFeatures();
  }, [selectedFilters, location]);

  const handleFilterChange = (filterName, value) => {
    setSelectedFilters(prevFilters => ({
      ...prevFilters,
      [filterName]: value
    }));
  };

  const handleCategoryChange = (e) => {
    setSelectedFilters(prev => ({ ...prev, category: e.target.value }));
  };

  const handlePriceChange = (min, max) => {
    setSelectedFilters(prev => ({ ...prev, priceMin: min, priceMax: max }));
  };

  const handleAvailabilityChange = (e) => {
    const value = e.target.value;
    setSelectedFilters(prev => ({
      ...prev,
      availability: prev.availability.includes(value)
        ? prev.availability.filter(item => item !== value)
        : [...prev.availability, value]
    }));
  };

  const handleBrandChange = (e) => {
    const value = e.target.value;
    setSelectedFilters(prev => ({
      ...prev,
      brands: prev.brands.includes(value)
        ? prev.brands.filter(item => item !== value)
        : [...prev.brands, value]
    }));
  };

  const handleFeatureChange = (e) => {
    const value = e.target.value;
    setSelectedFilters(prev => ({
      ...prev,
      features: prev.features.includes(value)
        ? prev.features.filter(item => item !== value)
        : [...prev.features, value]
    }));
  };

  if (loading) {
    return (
      <div className="text-center p-8">
        <div className="spinner-border animate-spin inline-block w-12 h-12 border-4 border-current border-t-transparent text-blue-500 rounded-full" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-wrap lg:flex-nowrap">
      <aside className="w-full lg:w-1/4 p-4 bg-white shadow-md rounded-lg lg:mr-4">
        <h2 className="text-xl font-semibold mb-4">Filters</h2>

        <div className="mb-6">
          <h3 className="font-semibold mb-2">Category</h3>
          <select
            value={selectedFilters.category}
            onChange={handleCategoryChange}
            className="block w-full bg-white border border-gray-400 px-4 py-2 rounded"
          >
            <option value="">All Categories</option>
            {categories.map(category => (
              <option key={category._id} value={category.name}>{category.name}</option>
            ))}
          </select>
        </div>

        <div className="mb-6">
          <h3 className="font-semibold mb-2">Price Range</h3>
          <input
            type="range"
            min="0"
            max="100"
            value={selectedFilters.priceMin}
            onChange={(e) => handlePriceChange(Number(e.target.value), selectedFilters.priceMax)}
            className="w-full mb-2"
          />
          <input
            type="range"
            min="0"
            max="100"
            value={selectedFilters.priceMax}
            onChange={(e) => handlePriceChange(selectedFilters.priceMin, Number(e.target.value))}
            className="w-full"
          />
          <div className="flex justify-between text-xs mt-2">
            <span>${selectedFilters.priceMin}</span>
            <span>${selectedFilters.priceMax}</span>
          </div>
        </div>

        <div className="mb-6">
          <h3 className="font-semibold mb-2">Availability</h3>
          <ul className="space-y-2">
            <li>
              <input
                type="checkbox"
                value="inStock"
                checked={selectedFilters.availability.includes('inStock')}
                onChange={handleAvailabilityChange}
                className="mr-2"
              />
              <label>In Stock</label>
            </li>
            <li>
              <input
                type="checkbox"
                value="outOfStock"
                checked={selectedFilters.availability.includes('outOfStock')}
                onChange={handleAvailabilityChange}
                className="mr-2"
              />
              <label>Out of Stock</label>
            </li>
          </ul>
        </div>

        <div className="mb-6">
          <h3 className="font-semibold mb-2">Brand</h3>
          <ul className="space-y-2">
            {brands.map(brand => (
              <li key={brand._id}>
                <input
                  type="checkbox"
                  value={brand.name}
                  checked={selectedFilters.brands.includes(brand.name)}
                  onChange={handleBrandChange}
                  className="mr-2"
                />
                <label>{brand.name}</label>
              </li>
            ))}
          </ul>
        </div>

        <div className="mb-6">
          <h3 className="font-semibold mb-2">Features</h3>
          <ul className="space-y-2">
            {features.map(feature => (
              <li key={feature._id}>
                <input
                  type="checkbox"
                  value={feature.name}
                  checked={selectedFilters.features.includes(feature.name)}
                  onChange={handleFeatureChange}
                  className="mr-2"
                />
                <label>{feature.name}</label>
              </li>
            ))}
          </ul>
        </div>
      </aside>

      <main className="w-full lg:w-3/4 p-4">
        <div className="flex justify-between items-center mb-6">
          <div>
            <label htmlFor="sort" className="font-semibold text-gray-700">Sort by:</label>
            <select id="sort" className="ml-2 p-2 border rounded bg-white shadow-sm">
              <option value="popularity">Popularity</option>
              <option value="price-low-high">Price: Low to High</option>
              <option value="price-high-low">Price: High to Low</option>
              <option value="newest">Newest</option>
            </select>
          </div>
          <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors duration-300">
            Apply Filters
          </button>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {parts.length > 0 ? (
            parts.map(part => (
              <div key={part._id} className="bg-white border border-gray-300 rounded-lg shadow-md p-4">
                <img src={part.imageUrl || 'default-image-url'} alt={part.name} className="w-full h-32 object-cover mb-4 rounded-md" />
                <h3 className="text-lg font-semibold mb-2">{part.name}</h3>
                <p className="text-gray-700 mb-2">Category: {part.category}</p>
                <p className="text-gray-700 mb-2">Price: ${part.price}</p>
                <p className="text-gray-700 mb-2">Brand: {part.brand}</p>
                <p className="text-gray-700 mb-4">Availability: {part.availability}</p>
                <Link to={`/parts/${part._id}`} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors duration-300">
                  View Details
                </Link>
              </div>
            ))
          ) : (
            <p>No parts found.</p>
          )}
        </div>
      </main>
    </div>
  );
}

export default PartCards;
