import React, { useState } from 'react';
import PropTypes from 'prop-types';

function FilterSidebar({ machines, selectedFilters, onFilterChange }) {
  const [expandedMachines, setExpandedMachines] = useState({});
  const [expandedSubsections, setExpandedSubsections] = useState({});

  const toggleMachine = (machineId) => {
    setExpandedMachines(prev => ({
      ...prev,
      [machineId]: !prev[machineId]
    }));
  };

  const toggleSubsection = (machineId, subsection) => {
    setExpandedSubsections(prev => ({
      ...prev,
      [`${machineId}-${subsection}`]: !prev[`${machineId}-${subsection}`]
    }));
  };

  const handleCheckboxChange = (machineId, subsection, value) => {
    onFilterChange(machineId, {
      ...selectedFilters[machineId],
      [subsection]: value
    });
  };

  return (
    <aside className="w-full lg:w-1/4 p-4 bg-white shadow-lg rounded-lg mb-6 lg:mb-0 lg:mr-6">
      <h2 className="text-xl font-bold text-gray-800 mb-4">Filter</h2>

      {machines.length > 0 ? (
        machines.map(machine => (
          <div key={machine._id} className="mb-6">
            <button 
              onClick={() => toggleMachine(machine._id)}
              className="w-full flex justify-between items-center text-gray-700 font-semibold mb-2"
            >
              {machine.name}
              <svg
                className={`w-4 h-4 transform transition-transform duration-300 ${expandedMachines[machine._id] ? 'rotate-180' : 'rotate-0'}`}
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
              </svg>
            </button>
            {expandedMachines[machine._id] && (
              <div className="pl-4">
                {machine.sections.map(section => (
                  <div key={section.name} className="mb-4">
                    <button 
                      onClick={() => toggleSubsection(machine._id, section.name)}
                      className="w-full flex justify-between items-center text-gray-600 font-medium mb-2"
                    >
                      {section.name}
                      <svg
                        className={`w-4 h-4 transform transition-transform duration-300 ${expandedSubsections[`${machine._id}-${section.name}`] ? 'rotate-180' : 'rotate-0'}`}
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
                      </svg>
                    </button>
                    {expandedSubsections[`${machine._id}-${section.name}`] && (
                      <div className="pl-4">
                        {section.options.map(option => (
                          <label key={option} className="flex items-center mb-2">
                            <input
                              type="checkbox"
                              className="form-checkbox h-4 w-4"
                              checked={selectedFilters[machine._id]?.[section.name]?.includes(option) || false}
                              onChange={(e) => handleCheckboxChange(machine._id, section.name, option)}
                            />
                            <span className="ml-2 text-gray-700">{option}</span>
                          </label>
                        ))}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        ))
      ) : (
        <p className="text-gray-500">No machines available for filtering.</p>
      )}
    </aside>
  );
}

FilterSidebar.propTypes = {
  machines: PropTypes.arrayOf(
    PropTypes.shape({
      _id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
      sections: PropTypes.arrayOf(
        PropTypes.shape({
          name: PropTypes.string.isRequired,
          options: PropTypes.arrayOf(PropTypes.string.isRequired).isRequired
        })
      ).isRequired
    })
  ).isRequired,
  selectedFilters: PropTypes.object.isRequired,
  onFilterChange: PropTypes.func.isRequired
};

export default FilterSidebar;
