import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';

function Navbar() {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role'); // Store the user role
  const history = useHistory();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role'); // Remove user role on logout
    history.push('/login');
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <nav className="bg-gray-900 p-4 shadow-lg">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-white text-xl font-bold">ðŸš— Auto Parts</Link>
        <div className="flex items-center space-x-4">
          <div className="relative">
            <button onClick={toggleDropdown} className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">
              Categories
            </button>
            {dropdownOpen && (
              <div className="absolute mt-2 w-48 bg-white rounded shadow-lg z-10">
                <Link to="/category/engine" className="block px-4 py-2 text-gray-800 hover:bg-gray-100">Engine Parts</Link>
                <Link to="/category/brakes" className="block px-4 py-2 text-gray-800 hover:bg-gray-100">Brakes</Link>
                <Link to="/category/transmission" className="block px-4 py-2 text-gray-800 hover:bg-gray-100">Transmission</Link>
                <Link to="/category/suspension" className="block px-4 py-2 text-gray-800 hover:bg-gray-100">Suspension</Link>
              </div>
            )}
          </div>
          {token ? (
            <>
              {role === 'admin' && <Link to="/add-part" className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">Add Part</Link>}
              <Link to="/profile" className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">Profile</Link>
              <Link to="/cart" className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">Cart</Link>
              <button onClick={handleLogout} className="bg-red-500 text-white px-3 py-2 rounded hover:bg-red-600 transition duration-300">Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">Login</Link>
              <Link to="/register" className="text-gray-300 hover:text-white px-3 py-2 rounded transition duration-300">Register</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
